$("i.fa.fa-search").click(function() {
	$("#search-field").width(150).css("opacity","1")
});

/* Smooth scroll */
$(document).ready(function() {
	$("a").on('click', function(event) {
		if (this.hash !== "") {
			event.preventDefault();
			var hash = this.hash;

			$('html, body').animate({
				scrollTop: $(hash).offset().top
			}, 800, function() {
				window.location.hash = hash;
			});
		}
	});
});